mapper.select:
SELECT 
FROM `collection_record`
WHERE id_config = #{idCardType,jdbcType=INTEGER}

    AND YEAR(collection_date) = #{year}

SELECT SUM(amount)
FROM `collection_record`
WHERE id_config = #{idCardType,jdbcType=INTEGER}

    AND YEAR(collection_date) = #{year}

SELECT count(1)
FROM collection_record cr
LEFT JOIN `user` u ON u.`id_user` = cr.`id_user`
where 1=1

    AND DATE_FORMAT(cr.`collection_date`,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and cr.id_card_type = #{idCardType,jdbcType=INTEGER}

SELECT cr.`id_collection_record` AS 'idCollectionRecord', cr.`amount`, cr.`collection_date` AS 'collectionDate', cr.`remark`, cr.`create_time` AS 'createTime', cr.id_card_type as 'idCardType', cr.id_config as 'idConfig',
cr.`id_user` AS 'idUser', u.`real_name` AS 'userName' FROM collection_record cr LEFT JOIN `user` u ON u.`id_user` = cr.`id_user` where 1=1
AND DATE_FORMAT(cr.`collection_date`,'%Y-%m-%d') BETWEEN #{collectionRecord.startTime} AND #{collectionRecord.endTime}
and cr.id_card_type = #{collectionRecord.idCardType,jdbcType=INTEGER}
ORDER BY cr.`collection_date` DESC
LIMIT #{startIndex},#{pageSize}
