mapper.select:
SELECT  FROM `payment_form`
WHERE id_user = #{idUser,jdbcType=INTEGER}
AND state = #{state,jdbcType=INTEGER}

SELECT
amount as 'collectionAmount',
collection_date as 'collectionDate'
FROM `collection_record`
WHERE id_card_type = #{idCardType,jdbcType=INTEGER}
AND  DATE_FORMAT(collection_date, '%Y-%m-%d') = #{currentDate}

SELECT pf.`code`, pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount',
pa.`amount` AS 'approvalAmount', pa.`create_time` AS 'approvalCreateTime',
pr.`amount` AS 'remittanceAmount', pr.`service_charge` AS 'serviceCharge', pr.`remittance_date` AS 'remittanceDate' FROM payment_form pf LEFT JOIN payment_approval pa ON pa.`id_payment_form` = pf.`id_payment_form` LEFT JOIN payment_remittance pr ON pr.`id_payment_form` = pf.`id_payment_form` WHERE pf.`id_card_type` = #{idCardType,jdbcType=INTEGER} AND DATE_FORMAT(pr.`remittance_date`, '%Y-%m-%d') = #{currentDate}

SELECT cr.`amount` as 'collectionAmount', cr.`id_card_type` as 'idCardType', cr.`id_config` as 'idConfig', ifr.`remaining_sum` AS 'remainingSum', ifr.`id_flow_type` AS 'idFlowType', ifr.`id_income_flow_record` AS 'idIncomeFlowRecord', cr.`remark` as 'remark', cr.`collection_date` as 'collectionDate', cr.`create_time` AS 'createTime' FROM income_flow_record ifr LEFT JOIN collection_record cr ON cr.`id_collection_record` = ifr.`id_collection_record` WHERE 1=1
AND DATE_FORMAT(cr.`collection_date`, '%Y-%m-%d') between #{startTime} and #{endTime}
ORDER BY cr.`collection_date` DESC, cr.`id_collection_record` DESC
LIMIT #{startIndex},#{pageSize}

SELECT pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`code`, pr.`id_card_type` as 'idCardType', pa.`amount` AS 'approvalAmount', pr.`amount` AS 'remittanceAmount', pr.`service_charge` AS 'serviceCharge', pfr.`remaining_sum` AS 'remainingSum', pfr.`id_flow_type` AS 'idFlowType', pfr.`id_pay_flow_record` AS 'idPayFlowRecord', pr.`remark` AS 'remark', pr.`remittance_date` as 'remittanceDate', pr.`id_config` as 'idConfig', pr.`create_time` AS 'createTime' FROM pay_flow_record pfr LEFT JOIN payment_remittance pr ON pr.`id_payment_remittance` = pfr.`id_payment_remittance` LEFT JOIN payment_approval pa ON pa.`id_payment_form` = pr.`id_payment_form` LEFT JOIN payment_form pf ON pf.`id_payment_form` = pa.`id_payment_form` WHERE 1=1
AND DATE_FORMAT(pr.`remittance_date`, '%Y-%m-%d') between #{startTime} and #{endTime}
ORDER BY pr.`remittance_date` DESC, pr.`id_payment_remittance` DESC
LIMIT #{startIndex},#{pageSize}

SELECT COUNT(1) FROM payment_form WHERE id_payment_form NOT IN (
    SELECT id_payment_form FROM payment_remittance WHERE id_payment_form IS NOT NULL
)

SELECT count(1) FROM payment_form pf LEFT JOIN payment_approval pa ON pa.`id_payment_form` = pf.`id_payment_form` LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` LEFT JOIN `user` ua ON ua.`id_user` = pa.`id_user` WHERE 1=1 AND pf.`state` <> 1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.id_card_type = #{paymentForm.idCardType,jdbcType=INTEGER}

SELECT pf.`id_payment_form` AS 'idPaymentForm', pf.`id_approval` as 'idApproval', pf.`code`, pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`id_payment_form_state` AS 'idPaymentFormState', pf.`create_time` AS 'createTime', pf.`id_user` AS 'idUser', pf.`id_card_type` as 'idCardType', pf.`id_config` as 'idConfig', pf.`state`, pf.`files`, pa.`amount` AS 'approvalAmount', ua.`real_name` AS 'approvalUser', pr.`amount` AS 'remittanceAmount', pr.`service_charge` AS 'serviceCharge', ur.`real_name` AS 'remittanceUser', u.`real_name` AS 'userName' FROM payment_form pf LEFT JOIN payment_approval pa ON pa.`id_payment_form` = pf.`id_payment_form` LEFT JOIN payment_remittance pr ON pr.`id_payment_form` = pf.`id_payment_form` LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` LEFT JOIN `user` ua ON ua.`id_user` = pa.`id_user` LEFT JOIN `user` ur ON ur.`id_user` = pr.`id_user` WHERE 1=1 AND pf.`state` <> 1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.id_card_type = #{paymentForm.idCardType,jdbcType=INTEGER}
ORDER BY pf.`create_time` DESC
LIMIT #{startIndex},#{pageSize}

SELECT count(1) FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE 1=1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}

SELECT pf.`id_payment_form` AS 'idPaymentForm', pf.`id_approval` as 'idApproval', pf.`code`, pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`id_payment_form_state` as 'idPaymentFormState', pf.`id_user` AS 'idUser', pf.`create_time` AS 'createTime', pf.`files`, u.`real_name` AS 'userName' FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE 1=1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}
ORDER BY pf.`create_time` DESC
LIMIT #{startIndex},#{pageSize}

SELECT pf.`id_payment_form` AS 'idPaymentForm', pf.`id_approval` as 'idApproval', pf.`code`, pf.`id_card_type` as 'idCardType', pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`id_payment_form_state` as 'idPaymentFormState', pf.`create_time` AS 'createTime', pf.`id_user` AS 'idUser', pf.`state`, pf.`files`, u.`real_name` AS 'userName' FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE pf.id_user = #{idUser,jdbcType=INTEGER}
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}
ORDER BY pf.`create_time` DESC
LIMIT #{startIndex},#{pageSize}
