mapper.select:
SELECT COUNT(1) FROM payment_form WHERE id_payment_form NOT IN (
    SELECT id_payment_form FROM payment_remittance
)

SELECT count(1) FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE 1=1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}

SELECT pf.`id_payment_form` AS 'idPaymentForm', pf.`code`, pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`id_payment_form_state` as 'idPaymentFormState', pf.`create_time` AS 'createTime', pf.`id_user` AS 'idUser',
u.`real_name` AS 'userName' FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE 1=1
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}
ORDER BY pf.`create_time` DESC
LIMIT #{startIndex},#{pageSize}

SELECT pf.`id_payment_form` AS 'idPaymentForm', pf.`code`, pf.`reason_application` AS 'reasonApplication', pf.`amount`, pf.`payment_name` AS 'paymentName', pf.`payment_account` AS 'paymentAccount', pf.`id_payment_form_state` as 'idPaymentFormState', pf.`create_time` AS 'createTime', pf.`id_user` AS 'idUser',
u.`real_name` AS 'userName' FROM payment_form pf LEFT JOIN `user` u ON u.`id_user` = pf.`id_user` WHERE pf.id_user = #{idUser,jdbcType=INTEGER}
AND pf.`code` LIKE CONCAT('%',#{paymentForm.code,jdbcType=VARCHAR},'%')
and pf.`id_payment_form_state` = #{paymentForm.idPaymentFormState,jdbcType=INTEGER}
ORDER BY pf.`create_time` DESC
LIMIT #{startIndex},#{pageSize}
